mapper.delete:
delete from members
where user_id = #{userId,jdbcType=BIGINT}
and group_id= #{groupId,jdbcType=BIGINT}

mapper.select:
   select group_id ,user_id 
   from members 
   where user_id = #{userId,jdbcType=BIGINT}
		and group_id= #{groupId,jdbcType=BIGINT}

mapper.update:
update members

	 status = #{status,jdbcType=VARCHAR},

where user_id = #{userId,jdbcType=BIGINT}
and group_id= #{groupId,jdbcType=BIGINT}
