mapper.delete:
delete from users
where id = #{id,jdbcType=INTEGER}
and id!='2'

mapper.update:
 update users
set  master='2'
 where master=#{master,jdbcType=VARCHAR}

mapper.select:
select id, username, password,nickname, type, status,score,
regist_time, picture_address,phone,last_login_time
from users
where 1=1
and username = #{username,jdbcType=VARCHAR}
and nickname = #{nickname,jdbcType=VARCHAR}
and type = #{type,jdbcType=VARCHAR}
and status = #{status,jdbcType=VARCHAR}
and score = #{score,jdbcType=VARCHAR}
and regist_time = #{registTime,jdbcType=TIMESTAMP}
and picture_address =
    #{pictureAddress,jdbcType=VARCHAR}

and phone = #{phone,jdbcType=VARCHAR}
and token = #{token,jdbcType=VARCHAR}
last_login_time = #{lastLoginTime,jdbcType=TIMESTAMP}
level = #{level,jdbcType=VARCHAR},
proxy_level = #{proxyLevel,jdbcType=VARCHAR},
expire_date = #{expireDate,jdbcType=DATE},

select id,username,nickname,type,status,picture_address,phone,level
,proxy_level,expire_date
from users where type = #{type}
and phone rlike #{keyWord}
order by ${orderBy}
desc
LIMIT #{pageNum},#{pageSize}
